import axios from "axios";
import React, { useState, useEffect } from "react";
import "../src/css/App.css";
import Cards from "./components/Cards.jsx";
import Nav from "./components/Nav.jsx";
// import videoo from "../src/Assets/cielo.mp4";
import Clime from "./Assets/Clime.png";

function App() {
  const apiKey = "4ae2636d8dfbdc3044bede63951a019b";

  const [cities, setCities] = useState([]);
  const [ setShowImage] = useState(true);

  useEffect(() => {
    const citiesFromStorage = localStorage.getItem("cities");
    if (citiesFromStorage) {
      setCities(JSON.parse(citiesFromStorage));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("cities", JSON.stringify(cities));
  }, [cities]);

  const onSearch = (ciudad) => {
  // Verificar si la ciudad ya existe en la lista
  const cityExists = cities.some((city) => city.name.toLowerCase() === ciudad.toLowerCase());

  if (cityExists) {
    // Mostrar una alerta o mensaje informando que la ciudad ya se encuentra en la lista
    alert(`${ciudad} ya se encuentra en la lista.`);
    return;
  }

  axios
    .get(
      `http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}&units=metric`
    )
    .then((response) => {
      setCities((oldCities) => [...oldCities, response.data]);
      setShowImage(false);
    })
    .catch((error) => {
      // Mostrar una alerta o mensaje informando que la ciudad no se pudo agregar
      alert(`Error al agregar ${ciudad}: ${error.message}`);
    });
};

  const onClose = (id) => {
    setCities((oldCities) => oldCities.filter((city) => city.id !== id));
  };

  return (
    <div className="App">
      <div>
        <Nav onSearch={onSearch} />
      </div>
      {cities.length ? (
        <div>
          <Cards cities={cities} onClose={onClose} />
        </div>
      ) : (
        <img src={Clime} alt="" style={{ width: "600px", margin: "3.5rem 0 0 0" }} />
      )}
      {/* <video autoPlay loop muted>
        <source src={videoo} type = "video/mp4" />
      </video> */}
    </div>
  );
}

export default App;
