import axios from "axios";
import React, { useState, useEffect } from "react";
import "../src/css/App.css";
import Cards from "./components/Cards.jsx";
import Nav from "./components/Nav.jsx";
import Clime from "./Assets/Clime.png";

function App() {
  const apiKey = "4ae2636d8dfbdc3044bede63951a019b";

  const [cities, setCities] = useState(JSON.parse(window.localStorage.getItem('cities')) || []);
  const [showImage, setShowImage] = useState(true);

  useEffect(() => {
    window.localStorage.setItem('cities', JSON.stringify(cities));
  }, [cities]);

  const onSearch = (ciudad) => {
    axios
      .get(
        `http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}&units=metric`
      )
      .then((response) => {
        setCities((oldCities) => [...oldCities, response.data]);
        setShowImage(false);
      });
  };

  const onClose = (id) => {
    setCities((oldCities) => oldCities.filter((city) => city.id !== id));
  };

  return (
    <div className="App">
      <div>
        <Nav onSearch={onSearch} />
      </div>
      {cities.length ? (
        <div>
          <Cards cities={cities} onClose={onClose} />
        </div>
      ) : (
        <img src={Clime} alt="" style={{ width: "600px", margin: "3.5rem 0 0 0" }} />
      )}
    </div>
  );
}

export default App;
