import React, { useState, useEffect } from "react";
import estilo from "../css/SearchBar.module.css";
import logo from "../Assets/wc.png";

export default function SearchBar({ onSearch }) {
  const [searchInput, setSearchInput] = useState("");
  const [cards, setCards] = useState([]);

  const handleSearch = async (searchInput) => {
    const results = await searchCities(searchInput);
    setCards(results);
  };

  useEffect(() => {
    const cardsWithFadeIn = cards.map((card) => ({ ...card, fadeIn: true }));
    setCards(cardsWithFadeIn);
  }, [cards]);

  const changeHandler = (event) => {
    setSearchInput(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch(searchInput);
    }
  };

  return (
    <div className={estilo.contenedor}>
      <img className={estilo.titulo} src={logo} />
      <input
        className={estilo.inputText}
        type="text"
        placeholder="Search for a city!"
        value={searchInput}
        onChange={changeHandler}
        onKeyPress={handleKeyPress}
      />
      <button className={estilo.boton} onClick={() => handleSearch(searchInput)}>
        <img
          src="https://th.bing.com/th/id/R.11f31fbdbe61951b4f85dd7618633183?rik=8esHaEmZbxfTaQ&riu=http%3a%2f%2ficon-icons.com%2ficons2%2f520%2fPNG%2f512%2fMagnifier_icon-icons.com_52024.png&ehk=bxd3uOf%2bLlcSv9l7Fy15AmfkZP10u75K7WVIPedD9v8%3d&risl=&pid=ImgRaw&r=0"
          className={estilo.img}
        />
      </button>
      <div className={estilo.cardContainer}>
        {cards.map((card) => (
          <div
            key={card.id}
            className={estilo.card + (card.fadeIn ? " fade-in" : "")}
          >
            <h2>{card.name}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
